import edu.duke.*;
import java.io.*;
import java.util.*;
import java.lang.*;

public class Part1 {
    public int findStopCodon(String dnaStr, int startIndex, String stopCodon) {
        int currIndex = dnaStr.indexOf(stopCodon, startIndex + 3);
        while (currIndex != -1) {
            int diff = currIndex - startIndex;
            if (diff % 3 == 0) {
                return currIndex;
            } else {
                currIndex = dnaStr.indexOf(stopCodon, currIndex + 1);
            }
        } return -1;
    }
    public String findGene(String dna, int where) {
        int startIndex = dna.indexOf("ATG", where);
        if (startIndex == -1) {
            return "";
        }
        int taaIndex = findStopCodon(dna, startIndex, "TAA");
        int tagIndex = findStopCodon(dna, startIndex, "TAG");
        int tgaIndex = findStopCodon(dna, startIndex, "TGA");
        int minIndex = 0;
        if (taaIndex == -1 || (tagIndex != -1 && tagIndex < taaIndex)) {
            minIndex = tagIndex;
        } else {
            minIndex = taaIndex;
        }
        if (minIndex == -1 || (tgaIndex != -1 && tgaIndex < minIndex)) {
            minIndex = tgaIndex;
        }
        if (minIndex == -1) {
            return "";
        }
        return dna.substring(startIndex, minIndex + 3);
    }
    public StorageResource getAllGenes(String dna) {
        StorageResource geneList = new StorageResource();
        int startIndex = 0;
        while (true) {
            String currentGene = findGene(dna, startIndex);
            if (currentGene.isEmpty()) {
                break;
            }
            geneList.add(currentGene);
            startIndex = dna.indexOf(currentGene, startIndex) + currentGene.length();
        }
        return geneList;
    }
    public String processCGRatio(String dna) {
        String currentGene = "";
        String txt = "";
        int startIndex = 0;
        while (true) {
            currentGene = findGene(dna, startIndex);
            float ratio = cgRatio(currentGene);
            ratio = ratio / currentGene.length();
            if (ratio < 0.35) {
                break;
            }
            txt = "The CG Ratio is equal to " + ratio;
            startIndex = dna.indexOf(currentGene, startIndex) + currentGene.length();
        }
        return txt;
    }
    public float cgRatio(String dna) {
        int len = dna.length();
        int c = 0;
        int g = 0;
        for (int i = 0; i < dna.length(); i++) {
            if (dna.charAt(i) == 'C') {
                c++;
            }
            if (dna.charAt(i) == 'G') {
                g++;
            }
        }
        float x = c + g;
        return x;
    }
    public String printAllGenes(String dna) {
        String currentGene = "";
        int startIndex = 0;
        while (true) {
            currentGene = findGene(dna, startIndex);
            if (currentGene.isEmpty()) {
                break;
            }
            System.out.println(currentGene);
            startIndex = dna.indexOf(currentGene, startIndex) + currentGene.length();
        }
        return currentGene;
    }
    public int findCTGCodon(String dna) {
        int count = 0;
        int currIndex = dna.indexOf("CTG");
        for (int i = 0; i < dna.length(); i++) {
            if (currIndex > 0) {
                count++;
                currIndex = dna.indexOf("CTG", currIndex + 3);
            }
        }
        return count;
    } 
    public void testNum() {
        String dna = "";
        testOn(dna);
    }
    public void testLength() {
        String dna = "";
        testOn(dna);
    }
    public void testOn(String dna) {
        System.out.println("Testing getAllGenes on" + dna);
        StorageResource genes = getAllGenes(dna);
        for (String g: genes.data()) {
            if (g.length() > 0) {
                System.out.println(g.length());
            }
        }
    }
    public void test() {
        String txt = processCGRatio("ATGATCTAATTTATGCTGCAACGGTGAAGA"); // .3793103448
        System.out.println(txt);
        
        txt = processCGRatio("ATGTTACCCCGGGGATGA");
        System.out.println(txt);
        
        txt = processCGRatio("ATGTCATAAGAAAGAAGATAATAGAGGGCCATGTAA");
        System.out.println(txt);
    }
}